import React, { useStae } from "react";
import theme from "./theme";
import { Editor, Node } from "./editor";
import {
  CssBaseline,
  MuiThemeProvider,
  Container,
  Card,
  CardContent,
  Typography,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
  root: {
    paddingTop: theme.spacing(10),
    paddingBottom: theme.spacing(4),
  },
  title: {
    margin: theme.spacing(0, 2, 2),
  },
  card: {
    marginBottom: theme.spacing(2),
  },
}));

const initialValue: Node[] = [
  {
    type: "paragraph",
    children: [{ text: "" }],
  },
];

export function App() {
  const [value, setValue] = React.useState(initialValue);
  const s = useStyles();

  return (
    <MuiThemeProvider theme={theme}>

      <CssBaseline />
      <Container className={s.root} maxWidth="sm">
      <button
              onClick={()=>{
                Editor.current!.addMark('bold')
                console.log('hey')
              }}
            >bold</button>
        <Typography className={s.title} component="h1" variant="h5">
          Slate.js Sandbox
        </Typography>
        <Card className={s.card} elevation={0}>

          <CardContent>

            <Editor
              value={value}
              onChange={x => {
                setValue(x);
                console.log(x, 8);
              }}
              renderMark={(props, _editor, next) => {
                if (props.mark.type === "bold") {
                  return (
                    <strong
                      style={{
                        letterSpacing: 1,
                        color: "pink"
                      }}
                    >
                      {props.children}
                    </strong>
                  );
                } else if (props.mark.type === "italic") {
                  return <em>{props.children}</em>;
                }

                return next();
              }}
              placeholder="Write text here..."
              autoFocus
              spellCheck
            />
          </CardContent>
        </Card>
      </Container>
    </MuiThemeProvider>
  );
}
